//To demonstrate the Bitwise operator 
package org.tnsif.operators;

public class BitwiseoperatorDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x=15,y=3;
		System.out.println(x&y);
		System.out.println(x|y);
		System.out.println(x^y);
		System.out.println(x>>y);
		System.out.println(x<<y);
		

	}

}
